var objects = [];

// たまがはねる
var obj = this.getObjectByName( 'Sphere1' );
objects.push( HANERU( obj ) );

// ハコがまわる
var obj = this.getObjectByName( 'Box1' );
objects.push( MAWARU( obj ) );

// えんすいがグルグル
var obj = this.getObjectByName( 'Cylinder1' );
objects.push( GURU1( obj ) );

// ハコがぐるぐる
var obj = this.getObjectByName( 'Box2' );
objects.push( GURU2( obj ) );

function update( event ) {

	for ( var i = 0; i < objects.length; i ++ ) {

		var obj = objects[ i ];

		if ( obj )
			obj.userData.update( obj, event);

	}

}

