// ポンポンはねる
function HANERU( obj ) {
	if ( !obj )
		return null;
	obj.userData.velocity = new THREE.Vector3();
	obj.userData.velocity.y = 0;
	obj.userData.position = new THREE.Vector3();
	obj.userData.position.x = obj.position.x;
	obj.userData.position.y = obj.position.y;
	obj.userData.position.z = obj.position.z;

	obj.userData.update = function( obj, event ) {
		var velocity = obj.userData.velocity;
		velocity.y -= 0.098;
		obj.position.add( velocity );
		if ( obj.position.y < 0 ) {
			obj.position.y = 0;
			velocity.y = - velocity.y;
			velocity.multiplyScalar( 0.6 );
			if ( velocity.y < 0.1 ) {
				velocity.x = Math.random() / 4;//- 0.5;
				velocity.y = Math.random() + 1;
				velocity.z = Math.random() / 4;//- 0.5;
				if ( obj.position.x > obj.userData.position.x ) {
					velocity.x = - velocity.x;
				}
				if ( obj.position.z > obj.userData.position.z ) {
					velocity.z = - velocity.z;
				}
			}
		}
	};
	return obj;
}

// 円でまわる かんすう
function MAWARU( obj ) {
	if ( !obj )
		return null;
	obj.userData.position = new THREE.Vector3();
	obj.userData.position.x = obj.position.x;
	obj.userData.position.y = obj.position.y;
	obj.userData.position.z = obj.position.z;
	obj.userData.update = function( obj, event ) {
		var time = event.time * 0.001;
		obj.position.x = obj.userData.position.x + Math.sin( time ) * 20;
		obj.position.z = obj.userData.position.z + Math.cos( time ) * 15;
	};
	return obj;
}

// グルグル回す かんすう
function GURU1( obj ) {
	if ( !obj )
		return null;
	obj.userData.update = function( obj, event ) {
		var time = event.time * 0.0001;
		obj.rotation.x = time * 10;
		obj.rotation.y = time * 20;
		obj.rotation.z = time * 60;
	};
	return obj;
}
function GURU2( obj ) {
	if ( !obj )
		return null;
	obj.userData.update = function( obj, event ) {
		var time = event.time * 0.0001;
		obj.rotation.x = time * 2;
		obj.rotation.y = time * 3;
		obj.rotation.z = time * 1;
	};
	return obj;
}

